package Vieky_String;

public class Upper_Lower_Case {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s1 = "VIEKY";
		String s2 = "vieky";
		
		System.out.println("Lower case is  = "+s1.toLowerCase());
		System.out.println();
		System.out.println("Upper case is  = "+s2.toUpperCase());
		
		

	}

}
